export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "권재현",
      },
      content: "1. 게시글 #해시태그 #해시태그 두번째",
      Images: [{
          src: "https://bookthumb-phinf.pstatic.net/cover/137/995/13799585.jpg?udate=20180726",
        }],
      Comments: [{
        User: {
          nickname: 'jay',
        },
        content: '우와 개정판이 나왔네요~',
      }, {
        User: {
          nickname: 'chris',
        },
        content: '얼른 사고 싶습니다.'
      }]
    }],
    imagePaths: [],
    postAdded: false,
};

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
}
const dummyPost = {
  id: 2,
  content: '안녕하세요.',
  User: {
    id: 1,
    nickname: 'jay',
  },
  Images: [],
  Comments: [],
};


const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
