import React, { useCallback, useState } from 'react';
import PropTypes from 'prop-types';


const PostImages = ({ images }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false);


    const onZoom = useCallback(() => {
        setShowImageZoom(true);
    }, [])
    if (images.length === 1) {
        return (
            <>
                <img role="presentation" src={images[0].src} alt={images[0].src} onClick={zoom} /> 
            </>
        )
    }
    if (images.length === 2) {
        return (
            <>
                <img role="presentation" width="50%" src={images[0].src} alt={images[0].src} onClick={zoom} /> 
                <img role="presentation" width="50%" src={images[1].src} alt={images[1].src} onClick={zoom} /> 
            </>
        )
    }



    return (
        <>
            <div>
                <img role="presentation" width="50%" src={images[0].src} alt={images[0].src} onClick={zoom} />
                <div
                    role="presentation"
                    style={{}}
                    onClick={onZoom}
                >
                    
                </div> 
            </div>
        </>
    )
};


PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages;
